import React, { Component } from 'react'
import Recipes from '../functional components/Recipes';
import RecipesDone from '../functional components/RecipesDone';

const InitRecipesList = [
    { image: 'data:image/jpeg;base64,', name: "pai", description: "good", value: 'prepare dish!' },
    { image: 'data:image/jpeg;base64,', name: "cake", description: "tasty", value: 'prepare dish!' },
    { image: 'data:image/jpeg;base64,', name: "weed", description: "felling highh", value: 'prepare dish!' }
];

const InitRecipesDone = [];

export default class MyKitchen extends Component {
    constructor(props) {
        super(props);

        this.state = {
            recipes: InitRecipesList,
            recipesDone: InitRecipesDone,
            counter: 0
        };
    }

    getRecipeToDone = (recipeName) => {
       
        let newRecipesList = this.state.recipes.filter(recipe => recipe.name !== recipeName);

        let recipeDone = this.state.recipes.filter(recipe => recipe.name == recipeName);
        console.log(recipeDone[0].value);

        recipeDone[0].value = "eat!";
 
        let newRecipesDone = [...this.state.recipesDone, recipeDone[0]];


        this.setState({ recipes: newRecipesList, recipesDone: newRecipesDone, counter: newRecipesDone.length });


        //console.log(newRecipesList);
        //console.log(this.state.recipes);
        //console.log(this.state.recipesDone);

    }

    getRecipeFromDone=(recipeName)=>{
        let recipeFromDone=this.state.recipesDone.filter(recipe=>recipe.name==recipeName);
        console.log(recipeFromDone);

        let newRecipesDoneList = this.state.recipesDone.filter(recipe => recipe.name !== recipeName);

        console.log(newRecipesDoneList);
        recipeFromDone[0].value = "prepare dish!";

        let newRecipesList=[...this.state.recipes,recipeFromDone[0]];
console.log(newRecipesList);

        this.setState({recipes:newRecipesList,recipesDone:newRecipesDoneList,counter:newRecipesDoneList.length});

        //console.log(this.state.recipes);
        //console.log(this.state.recipesDone);
    }
    render() {
        return (
            <div style={{  display: 'grid'}}>
                <Recipes recipes={this.state.recipes} setRecipeInDone={this.getRecipeToDone} /><br/>
                
                <RecipesDone recipesDone={this.state.recipesDone} counter={this.state.counter}  setRecipeFromDone={this.getRecipeFromDone}/>
            </div>
        )
    }
}
